import React from 'react'
import { useState } from 'react'
import Popup from 'reactjs-popup';
import { RotatingLines } from "react-loader-spinner";
import { useNavigate } from 'react-router-dom'
import './Ride.css'
import 'reactjs-popup/dist/index.css'

export default function RequestRide() {
  const sleep = ms => new Promise(r => setTimeout(r, ms));
  const navigate = useNavigate()
  const [isDataLoading, setIsDataLoading] = useState(false);

  const onCancelClick = () => {
    navigate('/home');
  }

  const onSubmitClick = () => {
    processData();
  }

  const onOKClick = () => {
    navigate('/home');
  }

  async function processData() {
    setIsDataLoading(true);
    await sleep(5000);
    <Popup>
      <p>Thank you, your ride is scheduled.</p>
      <button onClick={onOKClick}>OK</button>
    </Popup>
    navigate('/home');
  }

  if (isDataLoading) { 
    return (
      <RotatingLines
        strokeColor="black"
        strokeWidth="5"
        animationDuration="0.75"
        width="400"
        visible={true}
      />
    )
  } else {
    return (
      <div className={'mainContainer'}>
      <div className={'inputContainer'}>
        <p>Start address:</p>
        <input
          className={'inputBox'}
        />
        <button className={'mapSearch'} >Search...</button>
        <button className={'mapSearch'} >Current</button>
      </div>
      <div className={'inputContainer'}>
        <p>End address:</p>
        <input
          className={'inputBox'}
        />
        <button className={'mapSearch'} >Search...</button>
      </div>

      <div className={'inputContainer'}>
        <label for="vehilce">Vehicle Type</label>

        <select name="vehicle" id="vehicle">
          <option value="car">Car</option>
          <option value="truck">Truck</option>
          <option value="van">Van</option>
          <option value="shuttle">Shuttle</option>
        </select>
      </div>

      <div className={'submitContainer'}>
        <button className={'mapSearch'} onClick={onCancelClick} >Cancel</button>
        <Popup trigger=
          {<button className={'mapSearch'} onClick={onSubmitClick} >Submit</button>}
          modal nested>
            {processData()}
        </Popup>
      </div>
    </div>
    )
  }
}
