import React from 'react'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom'
import './Home.css'
import DropDownMenu from './DropDownMenu';

export default function Home() {
  const navigate = useNavigate()

  const [isDropdownVisible, setDropdownVisible] = useState(false);

  const handleMouseEnter = () => {
    setDropdownVisible(true);
  };

  const handleMouseLeave = () => {
    setDropdownVisible(false);
  };

  const onRequestClick = () => {
    navigate('/request')
  }

  const onFutureClick = () => {
    navigate('/future')
  }

  const onScheduleClick = () => {
    navigate('/schedule')
  }

  const onHistoryClick = () => {
    navigate('/history')
  }

  return (
    <>
      <div className={'mainContainer'}>
        <div className={'topContainer'}>
          <div className={'welcomeContainer'}>
            <p>Welcome to Jarvis Automotive Services!</p>
          </div>
          <div className={'accountContainer'}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave} >
              {isDropdownVisible && <DropDownMenu />}
          </div>
        </div>
        <div className={'homeContainer'}>
          <button className={'homeButton'} onClick={onRequestClick} >Request a Ride Now</button>
        </div>

        <div className={'homeContainer'}>
          <button className={'homeButton'} onClick={onFutureClick} >Request a Ride in the Future</button>
        </div>

        <div className={'homeContainer'}>
          <button className={'homeButton'} onClick={onScheduleClick} >View Scheduled Rides</button>
        </div>

        <div className={'homeContainer'}>
          <button className={'homeButton'} onClick={onHistoryClick} >Ride History</button>
        </div>
      </div>
    </>
  )
}
