import React, { useState, useRef, useEffect } from 'react'
import ScheduleList from './ScheduleList'
import {v4 as uuidv4} from 'uuid'

const LOCAL_STORAGE_KEY = 'scheduleApp.schedules'

export default function Schedule() {
  const [schedules, setSchedules] = useState([{ id: 1, pickup: '123 Apple Street', dropoff: '456 Banana Road', vehicle: 'car', daily: true, weekly: false, monthly: false}])
  const schedulePickupRef = useRef()
  const scheduleDropoffRef = useRef()
  const scheduleVehicleRef = useRef()
  const scheduleDailyRef = useRef()
  const scheduleWeeklyRef = useRef()
  const scheduleMonthlyRef = useRef()

  useEffect(() => {
    const storedSchedules = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY))
    if (storedSchedules) setSchedules(storedSchedules)
  }, [])

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(schedules))
  }, [schedules])

  function handleAddSchedule(e) {
    const pickup = schedulePickupRef.current.value
    const dropoff = scheduleDropoffRef.current.value
    const vehicle = scheduleVehicleRef.current.value
    const daily = scheduleDailyRef.current.value
    const weekly = scheduleWeeklyRef.current.value
    const monthly = scheduleMonthlyRef.current.value

    if (pickup === '') return
    setSchedules(prevSchedules => {
      return [...prevSchedules, {id: uuidv4(), pickup: pickup, dropoff: dropoff, vehicle: vehicle, daily: daily, weekly: weekly, monthly: monthly}]
    })
    schedulePickupRef.current.value = null
  }

  return (
    <div>
      <ScheduleList schedules={schedules} />
      <label for="pickup">Pickup:</label>
      <input name="pickup" ref={schedulePickupRef} type="text" />
      <label for="dropoff">Dropoff:</label>
      <input name="dropoff" ref={scheduleDropoffRef} type="text" />

      <label for="vehilce">Vehicle Type</label>

      <select name="vehicle" id="vehicle">
        <option value="car">Car</option>
        <option value="truck">Truck</option>
        <option value="van">Van</option>
        <option value="shuttle">Shuttle</option>
      </select>

      <button onClick={handleAddSchedule}>Add schedule</button>
    </div>
  )
}
