import { React } from 'react'
import Popup from 'reactjs-popup';
import './Schedule.css'

export default function ScheduleItem({ schedule, deleteSchedule }) {
  const [showModal, setShowModal] = useState(false)

  const handleCloseModal = () => {
    setShowModal(false)
  }
  const handleDelete = () => {
    deleteSchedule(schedule.id)
  }

  return (

    <div className="ScheduleRow">
        <label for="pickup">Pickup Address: </label>
        <p name="pickup" id="pickup">{schedule.pickup}</p>

        <label for="dropoff">Dropoff Address: </label>
        <p name="pidropoffckup" id="dropoff">{schedule.dropoff}</p>

        <label for="vehilce">Vehicle Type: </label>
        <p name="vehicle" id="vehicle">{schedule.vehicle}</p>

        <label for="daily">Daily</label>
        <input type="radio" id="daily" name="interval" defaultChecked={schedule.daily} disabled/>

        <label for="weekly">Weekly</label>
        <input type="radio" id="weedly" name="interval" defaultChecked={schedule.weekly} disabled/>

        <label for="monthly">Monthly</label>
        <input type="radio" id="monthly" name="interval" defaultChecked={schedule.monthly} disabled/>

        <button onClick={setShowModal(true)}>Delete</button>

        <Modal
          open={showModal}
          onRequestClose={handleCloseModal}
          className='modal'>
            <p>This will be permanently deleted, are you sure?</p>
            <button onClick={handleCloseModal}>Cancel</button>
            <button onClick={handleDelete}>Yes</button>
        </Modal>
    </div>
  )
}
