      return [...prevSchedules, {id: 1, pickup: pickup, dropoff: dropoff, vehicle: vehicle, date: date}]
    })
    scheduleNameRef.current.value = null
  }

  return (
    <div>
      <ScheduleList schedules={schedules} />
      <input ref={scheduleNameRef} type="text" />
      <button onClick={handleAddSchedule}>Add schedule</button>
    </div>
  )
}
