import { React, useState, useRef } from 'react'
import Popup from 'reactjs-popup';
import { useNavigate } from 'react-router-dom'
import './Ride.css'
import 'reactjs-popup/dist/index.css'
import moment from 'moment';

export default function RequestRide() {
  const navigate = useNavigate()

  const [fieldsNotValid, setFieldsNotValid] = useState(true)

  const startRef = useRef()
  const endRef = useRef()
  const vehicleRef = useRef()
  const pickupRef = useRef()

  const handleFieldsChange = () => {
    console.log(startRef.current.value)
    console.log(endRef.current.value)
    console.log(vehicleRef.current.value)
    console.log(pickupRef.current.value)

    var dateLimit = moment(pickupRef.current.value)
    var now = moment()

    var isValid = true
    if (!startRef || startRef.current.value === '') {
      isValid = false
    }
    if (!endRef || endRef.current.value === '') {
      isValid = false
    }
    if (!vehicleRef || vehicleRef === '') {
      isValid = false
    }
    if (!pickupRef || now.isAfter(pickupRef.current.value)) {
      isValid = false
    }

    setFieldsNotValid(!isValid)
  }

  const onCancelClick = () => {
    navigate('/home');
  }

  const onOKClick = () => {
    navigate('/home');
  }

  return (
    <div className={'mainContainer'}>
    <div className={'inputContainer'}>
      <p>Start address:</p>
      <input
        className={'inputBox'}
        ref={startRef}
        onChange={handleFieldsChange}
      />
      <button className={'mapSearch'} >Search...</button>
      <button className={'mapSearch'} >Current</button>
    </div>
    <div className={'inputContainer'}>
      <p>End address:</p>
      <input
        className={'inputBox'}
        ref={endRef}
        onChange={handleFieldsChange}
      />
      <button className={'mapSearch'} >Search...</button>
    </div>

    <div className={'inputContainer'}>
      <label for="vehilce">Vehicle Type</label>

      <select
          name="vehicle"
          ref={vehicleRef}
          id="vehicle"
          onChange={handleFieldsChange}>
        <option value="car">Car</option>
        <option value="truck">Truck</option>
        <option value="van">Van</option>
        <option value="shuttle">Shuttle</option>
      </select>
    </div>

    <div className={'inputContainer'}>
      <label for="datepicker">Pickup time:</label>
      <input
        name="datepicker" 
        ref={pickupRef}
        aria-label="Date and time"
        onChange={handleFieldsChange}
        type="datetime-local" />
    </div>

    <div className={'submitContainer'}>
      <button className={'mapSearch'} onClick={onCancelClick} >Cancel</button>
      <Popup trigger=
        {<button className={'mapSearch'} disabled={fieldsNotValid}>Submit</button>}
        modal nested className={'modal'}>
          <p>Thank you, your ride is scheduled.</p>
          <button onClick={onOKClick}>OK</button>
      </Popup>
    </div>
  </div>
  )
}
